mapper.update:
UPDATE codeGroup

SET 
	name = #{name}
WHERE
	seq = #{seq}

mapper.insert:
    	INSERT INTO `code`(
    		name
			, delNy
			, codeGroup_seq
		) VALUES (
			#{name}
			, 1
			, #{codeGroup_seq}
		)

mapper.delete:
UPDATE codeGroup

SET 
	delNy = 0
WHERE
	seq = #{seq}
	AND name = #{name}
