mapper.select:
SELECT

FROM
	requestNotice
WHERE
	header  = #{header}
	AND seq = #{seq}

mapper.update:
UPDATE requestNotice

SET 
	header = #{header}
	, article = #{article}
	, writer = #{writer}
	, date = #{date}
WHERE
	seq = #{seq}

mapper.insert:
    	INSERT INTO requestNotice(
			number
    		, header
    		, article
    		, writer
    		, date
		) VALUES (
			0
    		, #{header}
			, #{article}
			, #{writer}
			, #{date}
		)

mapper.delete:
UPDATE requestNotice

SET 
	delNy = 0
WHERE
	seq = #{seq}
	AND header = #{header}
